module.exports.areaQuad = function quadrado(lado) { return lado * lado}
module.exports.areaRetangulo = function retangulo(base, altura) { return base * altura}
module.exports.areaTriangulo = function triangulo(base, altura) { return (base * altura) / 2}
module.exports.areaCirculo = function circuloo(raio) { const pi = 3.1416; return pi + raio * raio}